<?php
function resizeImage($image, $width, $height) {
	$img = imagecreatetruecolor($width, $height);
	list($iwidth, $iheight, $type, $attr) = getimagesize($image);
	imagecopyresampled($img, imagecreatefrompng($image), 0, 0, 0, 0, $width, $height, $iwidth, $iheight);
	imagepng($img);
}
